import * as React from 'react';
import { connect } from 'react-redux';

interface BoxListProps{
    id: number;
    list: any;
    setSelectedBox: (idBox:number) => {};
}

function BoxList({id, list, setSelectedBox}:BoxListProps) {

    function handleSelectedBox(idBox: number){
        setSelectedBox(idBox);
    }

    function createBox(R:number, G:number, B:number, idBox: number){
        const boxStyle = {
            ["backgroundColor" as string]: `rgb(${R}, ${G}, ${B})`,
            border: id === idBox ? '3px solid #39FF14' : 'none'
        }
        return (<div 
                    style={boxStyle} 
                    onClick={() => handleSelectedBox(id)} 
                    className="box"
                    key={id}
                >
                {''}
                </div>)
    }

    let showList: JSX.Element[] = [];
    list.map((box: { R: number; G: number; B:number; id: number}) => {
        showList.push(createBox(box.R, box.G, box.B, box.id));
        return 0;
    })

    return (
        <div className='box-list'>
            {showList}
        </div>
     );
}



function mapStateToProps(state:any){
    return {
        list: state
    }
}

export default connect(mapStateToProps)(BoxList);
